mapper.update:
update ClubActivity set isDeleted=true

	and id=#{id}
	and clubId=#{clubId}
	and activityId=#{activityId}
	and createTime=#{createTime}

mapper.select:
select *from ClubActivity

	and id=#{id}
	and clubId=#{clubId}
	and activityId=#{activityId}
	and createTime=#{createTime}
	and isDeleted=#{isDeleted}
